// Check battery voltage - skip GSM if < 3.6V\nHourRecord& lastHour = rtc.hours[23];\nfloat batteryVoltage = lastHour.acc50 / 50.0f;\nbool batteryOK = (batteryVoltage >= 3.6f);\n\nif (!batteryOK) {\n    Serial.printf("[SEND] Battery too low (%.3fV) - GSM disabled\n", batteryVoltage);\n}\n\n// ================================\n// 1. GSM Init FIRST (–ü–ï–†–ï–î —Å–±–æ—Ä–∫–æ–π –æ—Ç—á–µ—Ç–∞!)\n// ================================\nif (cfg.gsmEnabled && batteryOK) {\n    // Enable SIM800L power\n    pinMode(SIM800L_POWER, OUTPUT);\n    digitalWrite(SIM800L_POWER, 1);\n    delay(2000); // Wait for module to power up\n\n    // üîç –ü–û–õ–ù–ê–Ø –û–¢–õ–ê–î–ö–ê SIM800 (–ë—É–¥–∏—Ç –º–æ–¥–µ–º –∏ —á–∏—Å—Ç–∏—Ç –±—É—Ñ–µ—Ä)\n    sim800Debug();\n\n    // –¢–µ–ø–µ—Ä—å –æ—Ñ–∏—Ü–∏–∞–ª—å–Ω–∞—è –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è\n    gsmOK = sim800Init();\n} else if (!cfg.gsmEnabled) {\n    Serial.printf("[SEND] GSM DISABLED in config.");\n} else {\n    Serial.printf("[SEND] GSM DISABLED - low battery (%.3fV < 3.6V)", batteryVoltage);\n}\n\n// ======================================================\n// 3. GSM ‚Üí SMS\n// ======================================================\nif (cfg.gsmEnabled && gsmOK && batteryOK)\n{\n    Serial.printf("[SEND] Sending via GSM...");\n\n    String sms = buildStatusSMS();\n    if (sms.length() > 400)\n        sms = sms.substring(0, 400);\n\n    bool s1 = sim800SendSMS(cfg.sms, sms);\n    bool s2 = sim800SendSMS(cfg.call, sms);\n\n    if (s1 || s2)\n        Serial.printf("[SEND] GSM SMS OK");\n    else\n        Serial.printf("[SEND] GSM SMS FAILED");\n    \n    // Power off SIM800L after SMS\n    digitalWrite(SIM800L_POWER, 0);\n    Serial.printf("[SEND] SIM800L powered off");\n}\nelse if (cfg.gsmEnabled && !batteryOK)\n{\n    Serial.printf("[SEND] GSM SKIPPED - battery too low (%.3fV)", batteryVoltage);\n}\nelse if (cfg.gsmEnabled && !gsmOK)\n{\n    Serial.printf("[SEND] GSM INIT FAILED ‚Üí SMS skipped.");\n    // Power off SIM800L if init failed\n    digitalWrite(SIM800L_POWER, 0);\n}